mapper.update:
    	UPDATE ApplyList
		SET
			Apply_proj_state = #{applyProjState},
			Apply_update_date = now()
		WHERE Apply_id = #{applyId}
		AND Apply_proj_id = #{applyProjId}

mapper.select:
SELECT 
	COUNT(*) 
FROM ApplyList 
WHERE apply_mem_id = #{applyMemId}
AND apply_proj_id = #{applyProjId} 
AND apply_type = #{applyType}
