mapper.update:
UPDATE ProjectList SET proj_mem_id = #{projMemId}, proj_title = #{projTitle}, proj_sort = #{projSort}, proj_indus = #{projIndus}, proj_price = #{projPrice}, proj_margin = #{projMargin}, proj_nation = #{projNation}, proj_supply_type = #{projSupplyType}, proj_recruit_num = #{projRecruitNum}, proj_detail = #{projDetail}, proj_require = #{projRequire}, proj_keyword = #{projKeyword}, proj_hit = #{projHit}, proj_channel = #{projChannel}, proj_profit = #{projProfit}, proj_profit_chk_date = #{projProfitChkDate}
proj_end_date = #{projEndDate},
proj_detail_img = #{projDetailImg},
proj_file = #{projFile},
proj_prod_certi = #{projProdCerti}, proj_state = #{projState}, proj_edit_date = now() where proj_id = #{projId}

mapper.select:
SELECT distinct proj_idx, proj_id, proj_mem_id, proj_title, proj_sort, i.indus_name as proj_indus_name, proj_indus, proj_price, proj_margin, proj_nation, proj_supply_type, proj_recruit_num, proj_detail, proj_detail_img, proj_require, proj_thumbnail_img, proj_keyword, proj_file, proj_prod_certi, proj_state,date_format(proj_reg_date, '%Y-%m-%d') as proj_reg_date, proj_hit, date_format(proj_end_date, '%Y-%m-%d') as proj_end_date, TO_DAYS(proj_end_date)-TO_DAYS(now()) as proj_Day, proj_edit_date, (SELECT COUNT(*) FROM ApplyList where apply_proj_id = p.Proj_id) as apply_count, proj_profit, proj_profit_chk_date, mem_rname, proj_channel, profile_biz_certi, mem_nick, mem_name, profile_intro, profile_photo, (select count(*) FROM ProjectList where Proj_mem_id = Profile_mem_id) as proj_add_count, (select count(*) FROM ApplyList where Apply_mem_id = Profile_mem_id and apply_type = 2) as recommend_count, (select count(*) FROM ApplyList where Apply_mem_id = Profile_mem_id and apply_proj_state = 5) as contract_count
, profile_biz_certi
, profile_biz_certi
, profile_ch_chk as seller_ch_chk
, profile_sale_chk as seller_sale_chk
FROM ProjectList p INNER JOIN IndusList i ON p.proj_indus = i.indus_id INNER JOIN MemList m ON p.proj_mem_id = m.mem_id INNER JOIN ProfileList pf ON p.proj_mem_id = pf.profile_mem_id
OR proj_detail LIKE concat('%', #{projDetail}, '%')
OR i.indus_name LIKE concat('%', #{projIndusName}, '%')
OR proj_keyword LIKE concat('%', #{projKeyword}, '%')
OR proj_mem_id = #{projMemId}
OR proj_title LIKE concat('%', #{projTitle}, '%')
AND proj_sort IN
#{item}
AND proj_nation IN
#{item}
AND proj_indus IN
#{item}
AND proj_price <= #{item}
AND proj_price >= #{item}
AND proj_margin IN
#{item}
AND proj_supply_type IN
#{item}
AND proj_prod_certi = #{projProdCerti}
AND proj_profit = #{projProfit}
AND profile_biz_certi = #{ppBizCerti} And proj_sort = 1
AND profile_biz_certi = #{sellerBizCerti} And proj_sort = 2
AND profile_ch_chk = #{sellerChChk}
AND profile_sale_chk = #{sellerSaleChk}
AND proj_channel Like concat('%', #{item}, '%')
AND mem_rname = #{sellerMemRname} AND proj_sort = "2" OR mem_rname = #{ppMemRname} AND proj_sort = "1"
AND mem_rname = #{ppMemRname} AND proj_sort = "1"
AND mem_rname = #{sellerMemRname} AND proj_sort = "2"
ORDER BY proj_reg_date DESC, proj_idx DESC
ORDER BY proj_end_date DESC
ORDER BY proj_reg_date DESC
ORDER BY apply_count DESC
ORDER BY proj_hit DESC
LIMIT #{offset}, #{listCount}

		SELECT COUNT(proj_idx)
